import { useState } from "react";
import { useQuery, useMutation } from "@apollo/client";
import { ALL_USERS } from "../graphql/queries/query";
import { CREATE_USER, DELETE_USER } from "../graphql/mutations/mutation";
import { useNavigate } from "react-router-dom";

export const Index = () => {
  //Variable para la navegaciÃ³n
  let navigate = useNavigate();

  //Control de la Modal
  const [show, setShow] = useState(false);

  const cerrar = () => {
    setShow(!show);
  };
  //---

  //Campos del formulario de la Modal
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [telefono, setTelefono] = useState("");
  const [correo, setCorreo] = useState("");
  const [contrasena, setContrasena] = useState("");
  const [username, setUsername] = useState("");
  const [rol, setRol] = useState("");

  const [createUsuario] = useMutation(CREATE_USER, {
    refetchQueries: [{ query: ALL_USERS }],
  });

  const submit = (event) => {
    event.preventDefault();
    console.log(event);

    createUsuario({
      variables: {
        nombre,
        apellido,
        telefono,
        correo,
        contrasena,
        username,
        rol,
      },
    });

    setNombre("");
    setApellido("");
    setTelefono("");
    setCorreo("");
    setContrasena("");
    setUsername("");
    setRol("");
  };
  //----

  //Funcion para eliminar un usuario
  const [deleteUsuario] = useMutation(DELETE_USER, {
    refetchQueries: [{ query: ALL_USERS }],
  });

  const deleteUser = (id) => {
    console.log(id);

    deleteUsuario({ variables: { id } });
  };
  //----

  //Se muestran las tuplas
  const { data, error, loading } = useQuery(ALL_USERS);

  if (loading)
    return (
      <>
        {/* <div className='loading-div'>
          <Spinner animation="border" variant="primary" />
          <div> */}
        <span> Cargando... </span>
        {/* </div>
        </div> */}
      </>
    );

  if (error) return `Error! ${error.message}`;

  return (
    <>
      <div className="content mt-5">
        <div className="container mt-2">
          <table className="table table-bordered table-hover">
            <thead className="table-active bg-primary">
              <tr align="center" className="columns">
                <th>Id</th>
                <th>Nombre</th>
                <th>Apellido</th>
                <th>Telefono</th>
                <th>Correo</th>
                <th>ContraseÃ±a</th>
                <th>Username</th>
                <th>Rol</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {data.listarUsers.map((user) => (
                <>
                  <tr align="center" key={user.id}>
                    <td>{user.Id}</td>
                    <td>{user.Nombre}</td>
                    <td>{user.Apellido}</td>
                    <td>{user.Telefono}</td>
                    <td>{user.Correo}</td>
                    <td>{user.Contrasena}</td>
                    <td>{user.Username}</td>
                    <td>{user.Rol}</td>

                    <td>
                      <button
                        type="button"
                        className="b1"
                        onClick={async () => {
                          navigate(`/actualizarusuario/${user.Id}`);
                        }}
                        key={user.Id}
                      >
                        Editar
                      </button>
                    </td>

                    <td>
                      <button
                        type="button"
                        className="b2"
                        onClick={() => deleteUser(user.Id)}
                        key={user.Id}
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                </>
              ))}
            </tbody>
          </table>

          {/* ---- */}

          <div className="buton-modal">
            <button className="btn btn-success px-2" onClick={cerrar}>
              {" "}
              Agregar nuevo usuario{" "}
            </button>
          </div>
        </div>
      </div>

      {/* Ventana Modal */}
      {/*  <div>
      

        <div className="modal-header p-5 pb-4 border-bottom-0 text">
         <div className="titulo-modal">
            <h2 className="fw-bold mb-0"><span class="badge bg-warning text-dark">Ingrese datos</span></h2>
         </div>

         <div className='abrir' >
         <button type="button" onClick={cerrar} class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
         </div>
         </div>

          <div class="modal-body p-5 pt-0">
            <form onSubmit={submit} >

              <input type='text' className='form-control mb-3' name={nombre} onChange={({ target }) => setNombre(target.value)} placeholder='Nombre' />

              <input type='text' className='form-control mb-3' name={apellido} onChange={({ target }) => setApellido(target.value)} placeholder='Apellido' />

              <input type='text' className='form-control mb-3' name={telefono} onChange={({ target }) => setTelefono(target.value)} placeholder='Telefono' />

              <input type='email' className='form-control mb-3' name={correo} onChange={({ target }) => setCorreo(target.value)} placeholder='Correo' />

              <input type='password' className='form-control mb-3' name={contrasena} onChange={({ target }) => setContrasena(target.value)}placeholder='ContraseÃ±a' />

              <input type='text' className='form-control mb-3' name={username} onChange={({ target }) => setUsername(target.value)} placeholder='Username' />

              <input type='text' className='form-control mb-3' name={rol} onChange={({ target }) => setRol(target.value)} placeholder='Rol' />

              <button id='button-cancel' className="btn btn-danger px-2" onClick={cerrar} > Cancelar </button>

              <button type='submit' className="btn btn-success px-2" onClick={cerrar} > Guardar </button>

            </form>
          </div>
       

    </div> */}
      {/* ---- */}

      <button
        type="button"
        class="btn btn-primary"
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
      >
        Launch demo modal
      </button>

      <div
        class="modal fade"
        id="exampleModal"
        tabindex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLabel">
                Modal title
              </h5>
              <button
                type="button"
                class="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div class="modal-body">...</div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button type="button" class="btn btn-primary">
                Save changes
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
